      throw json({message:`Unauthorized to ${method==="PATCH" ? "edit" : "add"} event.`},{status:500});
    }

    if(response.status===422){
        return response;
    }

    if(!response.ok){
        throw json({message:`failed to send ${method} request.`},{status:500});
    }

    return redirect("/events");

}
