import { Link, defer, json, useRouteLoaderData } from 'react-router-dom';
import classes from './EventDetails.module.css';
import EventsList from './EventsList';

export default function EventDetails(){

    const {eventKey, eventsKey}= useRouteLoaderData("event");

    function startDeleteHandler() {
        // ...
      }

    return <>
        <h1>Event Details.</h1>
        <article className={classes.event}>
        <img src={event.image} alt={event.title} />
        <h1>{event.title}</h1>
        <time>{event.date}</time>
        <p>{event.description}</p>
        <menu className={classes.actions}>
            <Link to="edit">Edit</Link>
            <button onClick={startDeleteHandler}>Delete</button>
        </menu>
        </article>
        <h1>All Events</h1>
        <EventsList events={eventsKey}/>
    </>
}


async function loadEventDetails({params}){
    const id= params.id;
    let url="http://localhost:8080/events/"+id;

    const response = await fetch(url);

    if(!response.ok){
        throw json({message:"Failed to fetch event details."},{status:500});
    }

    else{
        const {event} = await response.json();
        return event;
    }
}

async function loadEvents(){
    const response = await fetch("http://localhost:8080/events");

    if(!response.ok){
        throw json({message:"Failed to fetch events."},{status:500});
    }

    else{
        const {events} = await response.json();
        return events;
    }
}

export async function loader(){
    return defer({
        eventKey: await loadEventDetails(),
        eventsKey: loadEvents(),
    })
}
